package com.arjun.leetcode._686_repeated_string_match;

/**
 * 686. Repeated String Match This is a recursive solution. It is not the best solution. This leads
 * to stack overflow for large inputs. refer last test case.
 */
class Solution {
  public int repeatedStringMatch(String a, String b) {
    if (a.contains(b)) {
      return 1;
    }
    int count = 1;
    StringBuilder sb = new StringBuilder(a);
    return repeatedStringMatch(sb, a, b, count);
  }

  public int repeatedStringMatch(StringBuilder sb, String a, String b, int count) {
    if (sb.toString().contains(b)) {
      return count;
    }
    if (sb.length() > b.length() * 100) {
      return -1;
    }
    sb.append(a);
    return repeatedStringMatch(sb, a, b, ++count);
  }

  public static void main(String[] args) {
    int i = new Solution().repeatedStringMatch("abcd", "cdabcdab");
    System.out.println(i);

    i =
        new Solution()
            .repeatedStringMatch("wewewewewewewe", "wewewewewewewewewewewewewewewewewewewewewewe");
    System.out.println(i);

    i = new Solution().repeatedStringMatch("bbbb", "aaaaaaaaaaaaaaaaaaaaa");
    System.out.println(i);

    i = new Solution().repeatedStringMatch("aaaaaaaaaaaaaaaaaaaaaab", "ba");
    System.out.println(i);

    i =
        new Solution()
            .repeatedStringMatch(
                "a",
                "");
    System.out.println(i);
  }
}
