package com.arjun.leetcode._686_repeated_string_match;

/** 686. Repeated String Match This is a better solution than recursive solution. */
public class Solution1 {
  public int repeatedStringMatch(String a, String b) {
    int count = 1;
    StringBuilder sb = new StringBuilder(a);
    while (sb.length() < b.length()) {
      sb.append(a);
      count++;
    }
    if (sb.indexOf(b) >= 0) {
      return count;
    }
    if (sb.append(a).indexOf(b) >= 0) {
      return count + 1;
    }
    return -1;
  }

  public static void main(String[] args) {
    int i = new Solution1().repeatedStringMatch("abcd", "cdabcdab");
    System.out.println(i);

    i =
        new Solution1()
            .repeatedStringMatch("wewewewewewewe", "wewewewewewewewewewewewewewewewewewewewewewe");
    System.out.println(i);

    i = new Solution1().repeatedStringMatch("bbbb", "aaaaaaaaaaaaaaaaaaaaa");
    System.out.println(i);

    i = new Solution1().repeatedStringMatch("aaaaaaaaaaaaaaaaaaaaaab", "ba");
    System.out.println(i);

    i =
        new Solution1()
            .repeatedStringMatch(
                "a",
                "");
    System.out.println(i);
  }
}
